import React, { useState } from 'react';
import { Button, TextField, Grid, FormControl, InputLabel, Select, MenuItem } from '@mui/material';
import axios from 'axios';
import { auth } from '../firebase';

const TimetableForm = () => {
    const [hoursPerDay, setHoursPerDay] = useState(5);
    const [subjects, setSubjects] = useState({});
    const [dayOrder, setDayOrder] = useState(1); // Default Day Order (Monday)

    const handleSaveTimetable = async () => {
        const user = auth.currentUser;
        if (user) {
            try {
                await axios.post(`/api/timetable/`, {
                    googleAuthId: user.uid,
                    dayOrder,
                    subjects
                });
                alert('Timetable saved!');
            } catch (error) {
                console.error('Error saving timetable:', error);
            }
        }
    };

    const handleChangeSubject = (hour, subject) => {
        setSubjects((prev) => ({
            ...prev,
            [hour]: subject
        }));
    };

    return (
        <div>
            <FormControl fullWidth>
                <InputLabel>Hours per Day</InputLabel>
                <Select
                    value={hoursPerDay}
                    onChange={(e) => setHoursPerDay(e.target.value)}
                    label="Hours per Day"
                >
                    {[...Array(6).keys()].map(i => (
                        <MenuItem value={i + 1} key={i + 1}>{i + 1} hours</MenuItem>
                    ))}
                </Select>
            </FormControl>

            {[...Array(hoursPerDay).keys()].map(hourIndex => (
                <Grid container spacing={2} key={hourIndex}>
                    <Grid item xs={12} sm={2}>
                        <TextField
                            label={`Hour ${hourIndex + 1}`}
                            onChange={(e) => handleChangeSubject(hourIndex + 1, e.target.value)}
                        />
                    </Grid>
                </Grid>
            ))}
            <Button onClick={handleSaveTimetable} variant="contained">Save Timetable</Button>
        </div>
    );
};

export default TimetableForm;
