import React, { useState } from 'react';
import { Button, TextField } from '@mui/material';
import axios from 'axios';

const AdminConfigForm = () => {
    const [date, setDate] = useState('');
    const [timetableOrder, setTimetableOrder] = useState('');

    const handleSaveConfig = async () => {
        try {
            await axios.post('/api/admin-config', { academicDate: date, timetableOrder: timetableOrder });
            alert('Configuration saved!');
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div>
            <TextField label="Academic Date" onChange={(e) => setDate(e.target.value)} />
            <TextField label="Timetable Order" onChange={(e) => setTimetableOrder(e.target.value)} />
            <Button onClick={handleSaveConfig} variant="contained">Save Configuration</Button>
        </div>
    );
};

export default AdminConfigForm;
