import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const ChatHistory = () => {
    const [chats, setChats] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch all chats when the component mounts
        axios.get('/chats')
            .then(response => {
                setChats(response.data);
            })
            .catch(error => {
                console.error('Error fetching chats:', error);
            });
    }, []);

    const handleChatClick = (chatId) => {
        // Navigate to the chat page with the selected chat ID
        navigate(`/chat/${chatId}`);
    };

    const handleCreateChat = () => {
        // Create a new chat
        axios.post('/chats', { name: 'New Chat' })
            .then(response => {
                const newChatId = response.data.id;
                navigate(`/chat/${newChatId}`);
            })
            .catch(error => {
                console.error('Error creating new chat:', error);
            });
    };

    return (
        <div>
            <h1>Chat History</h1>
            <button onClick={handleCreateChat}>+ New Chat</button>
            <ul>
                {chats.map(chat => (
                    <li key={chat.id} onClick={() => handleChatClick(chat.id)}>
                        {chat.name}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ChatHistory;
