import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { UserContext } from '../Contexts/User';
import { Container, Typography, Box, Button, RadioGroup, FormControlLabel, Radio } from '@mui/material';

const IntroductoryQuiz = () => {
    const { courseId } = useParams();
    const [quiz, setQuiz] = useState(null);
    const [answers, setAnswers] = useState({});
    const { user } = useContext(UserContext);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchQuiz = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/courses/${courseId}/introductory-quiz`);
                setQuiz(response.data);
                // Initialize answers object
                const initialAnswers = {};
                response.data.questions.forEach(question => {
                    initialAnswers[question.id] = '';
                });
                setAnswers(initialAnswers);
            } catch (error) {
                console.error('Error fetching quiz:', error);
            }
        };

        fetchQuiz();
    }, [courseId]);

    const handleAnswerChange = (questionId, optionIndex) => {
        setAnswers(prevAnswers => ({
            ...prevAnswers,
            [questionId]: optionIndex
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!quiz || !user) {
            alert('Quiz or user data is missing');
            return;
        }

        // Check if all questions are answered
        const unansweredQuestions = quiz.questions.filter(question => answers[question.id] === '');
        if (unansweredQuestions.length > 0) {
            alert('Please answer all questions before submitting.');
            return;
        }

        try {
            // Format quiz submission data to match JPA entity structure
            const quizSubmission = {
                id: null, // Let JPA generate the ID
                quiz: {
                    id: quiz.id
                },
                user: {
                    id: user.id
                },
                questionSummaries: quiz.questions.map(question => {
                    const selectedOptionIndex = parseInt(answers[question.id]);
                    return {
                        id: null, // Let JPA generate the ID
                        question: {
                            id: question.id
                        },
                        selectedOption: selectedOptionIndex,
                        isCorrect: question.options[selectedOptionIndex]?.isCorrect || false
                    };
                })
            };

            console.log('Submitting quiz:', JSON.stringify(quizSubmission, null, 2));

            const response = await axios.post(
                `http://localhost:8080/users/${user.id}/quiz-summary/${quiz.id}`, 
                quizSubmission,
                {
                    headers: {
                        'Content-Type': 'application/json'
                    }
                }
            );

            console.log('Server response:', JSON.stringify(response.data, null, 2));

            // Check response data structure
            if (response.data && typeof response.data === 'object') {
                if (response.data.id) {
                    navigate(`/quiz-summary/${response.data.id}`);
                } else {
                    console.error('Response data structure:', response.data);
                    throw new Error('Quiz summary ID not found in response');
                }
            } else {
                console.error('Unexpected response format:', response.data);
                throw new Error('Invalid response format from server');
            }
        } catch (error) {
            console.error('Error details:', {
                message: error.message,
                response: error.response?.data,
                status: error.response?.status
            });

            const errorMessage = error.response?.data?.message || 
                               (typeof error.response?.data === 'string' ? error.response.data : null) || 
                               error.message || 
                               'Failed to submit quiz';
            alert(`Error submitting quiz: ${errorMessage}`);
        }
    };

    if (!quiz) {
        return <div>Loading...</div>;
    }

    return (
        <Container>
            <Box mt={4}>
                <Typography variant="h4" component="h1" gutterBottom>
                    {quiz.name}
                </Typography>
                <Typography variant="body1" gutterBottom color="textSecondary">
                    Please answer all questions to proceed with the course.
                </Typography>
                <form onSubmit={handleSubmit}>
                    {quiz.questions.map((question, questionIndex) => (
                        <Box key={question.id} mt={4} mb={4} p={2} border={1} borderRadius={1} borderColor="divider">
                            <Typography variant="h6" component="h2" gutterBottom>
                                {`${questionIndex + 1}. ${question.name}`}
                            </Typography>
                            <RadioGroup
                                value={answers[question.id]}
                                onChange={(e) => handleAnswerChange(question.id, e.target.value)}
                            >
                                {question.options.map((option, optionIndex) => (
                                    <FormControlLabel
                                        key={optionIndex}
                                        value={optionIndex}
                                        control={<Radio />}
                                        label={option.text}
                                    />
                                ))}
                            </RadioGroup>
                        </Box>
                    ))}
                    <Box mt={4} mb={4} display="flex" justifyContent="center">
                        <Button 
                            type="submit" 
                            variant="contained" 
                            color="primary" 
                            size="large"
                        >
                            Submit Quiz
                        </Button>
                    </Box>
                </form>
            </Box>
        </Container>
    );
};

export default IntroductoryQuiz;
