import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { auth, signInWithGoogle } from "../firebase";
import { onAuthStateChanged } from "firebase/auth";

const LoginComponent = () => {
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, async (user) => {
            if (user) {
                setLoading(true);
                try {
                    const email = user.email;
                    const response = await axios.get(`http://localhost:8080/users/exists/${email}`);

                    if (response.data) {
                        navigate("/attendance"); // Existing user → Dashboard
                    } else {
                        // Send GoogleAuthID & PhotoURL but don't show them to user
                        navigate(`/register`, { state: { 
                            googleAuthId: user.uid, 
                            email: user.email, 
                            photoURL: user.photoURL 
                        }});
                    }
                } catch (error) {
                    console.error("Error checking user:", error);
                } finally {
                    setLoading(false);
                }
            }
        });

        return () => unsubscribe();
    }, [navigate]);

    const handleLogin = async () => {
        setLoading(true);
        const user = await signInWithGoogle();
        if (!user) {
            setLoading(false);
            return;
        }

        try {
            const email = user.email;
            const response = await axios.get(`http://localhost:8080/api/users/exists/${email}`);

            if (response.data) {
                navigate("/dashboard"); // Existing user → Dashboard
            } else {
                navigate(`/register`, { state: { 
                    googleAuthId: user.uid, 
                    email: user.email, 
                    photoURL: user.photoURL 
                }});
            }
        } catch (error) {
            console.error("Error checking user:", error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ textAlign: "center", marginTop: "50px" }}>
            <h2>Welcome to Our Platform</h2>
            {loading ? <p>Loading...</p> : <button onClick={handleLogin}>Sign in with Google</button>}
        </div>
    );
};

export default LoginComponent;
