import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { CircularProgress } from "@mui/material";
import { auth } from "./firebase"; // Firebase authentication
import DashboardComponent from "./components/Dashboard";
import LoginComponent from "./components/Login";
import Register from "./components/Registration"; // Added Register Component
import ProfileView from "./components/ProfileView";
import TimetableManagement from "./components/TimetableManagement";
import AdminConfigForm from "./components/AdminConfigForm";
import axios from "axios";

const App = () => {
    const [user, setUser] = useState(null); // Track logged-in user
    const [loading, setLoading] = useState(true); // Loading state while checking user authentication
    const [isNewUser, setIsNewUser] = useState(false); // Track if user is new

    useEffect(() => {
        // Check Firebase Authentication
        const unsubscribe = auth.onAuthStateChanged(async (currentUser) => {
            setUser(currentUser); // Update user state

            if (currentUser) {
                try {
                    // Check if the user exists in the backend database
                    const response = await axios.get(`http://localhost:8080/api/users/check/${currentUser.email}`);

                    if (!response.data) {
                        setIsNewUser(true); // New user → Redirect to registration
                    } else {
                        setIsNewUser(false); // Existing user → Allow access
                    }
                } catch (error) {
                    console.error("Error checking user existence:", error);
                }
            }
            setLoading(false);
        });

        return () => unsubscribe(); // Clean up the listener on unmount
    }, []);

    // Private Route for authenticated users (existing users only)
    const PrivateRoute = ({ children }) => {
        if (!user) return <Navigate to="/login" />;
        if (isNewUser) return <Navigate to="/register" />; // Redirect new users to register
        return children;
    };

    return (
        <Router>
            {loading ? (
                <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100vh" }}>
                    <CircularProgress />
                </div>
            ) : (
                <Routes>
                    {/* Public Route for Login */}
                    <Route path="/login" element={<LoginComponent />} />

                    {/* Public Route for Registration (only for new users) */}
                    <Route path="/register" element={<Register/>} />

                    {/* Private route for Dashboard */}
                    <Route
                        path="/dashboard"
                        element={
                            <PrivateRoute>
                                <DashboardComponent />
                            </PrivateRoute>
                        }
                    />

                    {/* Private route for Profile */}
                    <Route
                        path="/profile"
                        element={
                            <PrivateRoute>
                                <ProfileView />
                            </PrivateRoute>
                        }
                    />

                    {/* Private route for Timetable Management */}
                    <Route
                        path="/timetable"
                        element={
                            <PrivateRoute>
                                <TimetableManagement />
                            </PrivateRoute>
                        }
                    />

                    {/* Admin Route for Admin Config */}
                    <Route
                        path="/admin"
                        element={
                            <PrivateRoute>
                                <AdminConfigForm />
                            </PrivateRoute>
                        }
                    />

                    {/* Redirect authenticated users to Dashboard */}
                    <Route path="/" element={user ? <Navigate to="/dashboard" /> : <Navigate to="/login" />} />
                </Routes>
            )}
        </Router>
    );
};

export default App;
