import { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";

const RegisterComponent = () => {
    const location = useLocation();
    const navigate = useNavigate();

    // Retrieve GoogleAuthID, Email, and PhotoURL from LoginComponent
    const { googleAuthId, email, photoURL } = location.state || {};

    const [formData, setFormData] = useState({
        name: "",
        department: "",
        yearOfStudy: "",
        rollNo: "",
        googleAuthId: googleAuthId || "",
        email: email || "",
        photoURL: photoURL || ""
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            await axios.post("http://localhost:8080/api/users/register", formData);
            navigate("/dashboard"); // Redirect to dashboard after successful registration
        } catch (error) {
            console.error("Error registering user:", error);
        }
    };

    return (
        <div style={{ textAlign: "center", marginTop: "50px" }}>
            <h2>Complete Your Registration</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" name="name" placeholder="Name" onChange={handleChange} required />
                <input type="text" name="department" placeholder="Department" onChange={handleChange} required />
                <input type="number" name="yearOfStudy" placeholder="Year of Study" onChange={handleChange} required />
                <input type="text" name="rollNo" placeholder="Roll No" onChange={handleChange} required />

                {/* Display Pre-filled Google Data (Read-only) */}
                <input type="email" name="email" value={formData.email} readOnly />
                <input type="text" name="googleAuthId" value={formData.googleAuthId} readOnly />
                <input type="text" name="photoURL" value={formData.photoURL} readOnly />

                <button type="submit">Register</button>
            </form>
        </div>
    );
};

export default RegisterComponent;
