import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { UserContext } from '../Contexts/User';
import { 
    Container, 
    Typography, 
    Box, 
    Button, 
    List, 
    ListItem, 
    ListItemIcon, 
    ListItemText,
    Paper,
    CircularProgress
} from '@mui/material';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import CancelIcon from '@mui/icons-material/Cancel';

const QuizSummary = () => {
    const { quizId } = useParams();
    const [summary, setSummary] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const { user } = useContext(UserContext);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchSummary = async () => {
            if (!user || !quizId) return;

            try {
                setLoading(true);
                setError(null);

                const response = await axios.get(
                    `http://localhost:8080/users/${user.id}/quiz-summary/${quizId}`
                );

                if (response.data) {
                    setSummary(response.data);
                } else {
                    throw new Error('No quiz summary data received');
                }
            } catch (error) {
                console.error('Error fetching quiz summary:', error);
                // Handle different types of error responses
                const errorMessage = error.response?.data?.message || 
                                   error.response?.data || 
                                   error.message || 
                                   'Failed to load quiz summary';
                setError(errorMessage);
            } finally {
                setLoading(false);
            }
        };

        fetchSummary();
    }, [quizId, user]);

    const getNextLessonId = () => {
        if (!summary?.quiz?.course) return null;
        const course = summary.quiz.course;

        // For introductory quiz, return the first lesson's ID
        if (isQuizType('INTRODUCTORY')) {
            return course.lessons[0]?.id || null;
        }

        // For lesson quiz, find next lesson
        if (summary.quiz.lessonId) {
            const currentLessonIndex = course.lessons.findIndex(lesson => lesson.id === summary.quiz.lessonId);
            return currentLessonIndex < course.lessons.length - 1 ? course.lessons[currentLessonIndex + 1].id : null;
        }

        return null;
    };

    const handleContinue = async () => {
        try {
            const course = summary.quiz.course;
            const passed = calculateScore() >= getPassingScore();

            if (passed) {
                switch(summary.quiz.type) {
                    case 'FINAL':
                        await axios.post(`http://localhost:8080/users/${user.id}/complete/${course.id}`);
                        navigate('/certificates');
                        break;
                    case 'INTRODUCTORY':
                        const firstLessonId = course.lessons[0]?.id;
                        if (firstLessonId) {
                            navigate(`/learn/${course.id}/${firstLessonId}`);
                        } else {
                            navigate(`/courses/${course.id}`);
                        }
                        break;
                    default: // Lesson quiz
                        const nextLessonId = getNextLessonId();
                        if (nextLessonId) {
                            navigate(`/learn/${course.id}/${nextLessonId}`);
                        } else {
                            // If no next lesson, redirect to final quiz
                            navigate(`/quiz/${course.finalQuiz.id}`);
                        }
                }
            } else {
                navigate(`/courses/${course.id}`);
            }
        } catch (error) {
            console.error('Error handling navigation:', error);
        }
    };

    const calculateScore = () => {
        if (!summary?.questionSummaries?.length) return 0;
        const correctAnswers = summary.questionSummaries.filter(qs => qs.isCorrect).length;
        return Math.round((correctAnswers / summary.questionSummaries.length) * 100);
    };

    const getPassingScore = () => {
        return isQuizType('FINAL') ? 80 : 60;
    };

    const isQuizType = (type) => {
        return summary.quiz.type === type;
    };

    // Show loading state
    if (loading) {
        return (
            <Container>
                <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
                    <CircularProgress />
                </Box>
            </Container>
        );
    }

    // Show error state
    if (error || !summary) {
        return (
            <Container>
                <Box mt={4} textAlign="center">
                    <Typography variant="h6" color="error" gutterBottom>
                        {typeof error === 'string' ? error : 'Failed to load quiz summary'}
                    </Typography>
                    <Button 
                        variant="contained" 
                        color="primary" 
                        onClick={() => navigate(-1)}
                        sx={{ mt: 2 }}
                    >
                        Go Back
                    </Button>
                </Box>
            </Container>
        );
    }

    const score = calculateScore();
    const passed = score >= getPassingScore();

    return (
        <Container maxWidth="md">
            <Box mt={4} mb={4}>
                <Paper elevation={3} sx={{ p: 4 }}>
                    <Typography variant="h4" align="center" gutterBottom>
                        {summary.quiz.name} Results
                    </Typography>

                    <Box mt={4} textAlign="center">
                        <Typography variant="h1" 
                            color={passed ? 'success.main' : 'error.main'}
                            sx={{ mb: 2 }}
                        >
                            {score}%
                        </Typography>
                        <Typography variant="h5" 
                            color={passed ? 'success.main' : 'error.main'}
                            sx={{ mb: 3 }}
                        >
                            {passed ? 'Congratulations! You Passed!' : 'Please Try Again'}
                        </Typography>
                    </Box>

                    <List sx={{ mt: 4 }}>
                        {summary.questionSummaries.map((qs, index) => (
                            <ListItem key={index} sx={{ mb: 2, bgcolor: 'background.paper', borderRadius: 1 }}>
                                <ListItemIcon>
                                    {qs.isCorrect ? 
                                        <CheckCircleIcon color="success" /> : 
                                        <CancelIcon color="error" />
                                    }
                                </ListItemIcon>
                                <ListItemText
                                    primary={qs.question.name}
                                    secondary={
                                        <>
                                            <Typography component="span" color="textSecondary">
                                                Your answer: {qs.selectedOption?.text}
                                            </Typography>
                                            {!qs.isCorrect && (
                                                <Typography component="span" color="error" sx={{ display: 'block' }}>
                                                    Correct answer: {qs.question.options.find(opt => opt.isCorrect)?.text}
                                                </Typography>
                                            )}
                                        </>
                                    }
                                />
                            </ListItem>
                        ))}
                    </List>

                    <Box mt={4} textAlign="center">
                        <Button
                            variant="contained"
                            color="primary"
                            size="large"
                            onClick={handleContinue}
                        >
                            {passed ? 
                                (isQuizType('FINAL') ? 'View Certificate' : 'Continue to Next Lesson') : 
                                'Return to Course'}
                        </Button>
                    </Box>
                </Paper>
            </Box>
        </Container>
    );
};

export default QuizSummary;
