import React, { useState, useEffect } from "react";
import axios from "axios";

const Attendance = ({ userId }) => {
    const [attendanceData, setAttendanceData] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [isDataAvailable, setIsDataAvailable] = useState(false);

    // Check if attendance data exists for the user
    useEffect(() => {
        const checkAttendance = async () => {
            try {
                const response = await axios.get(
                    `http://localhost:8080/attendance/exists?userId=${userId}`
                );
                if (response.data) {
                    setIsDataAvailable(true);
                    const dataResponse = await axios.get(
                        `http://localhost:8080/attendance/${userId}`
                    );
                    setAttendanceData(dataResponse.data);
                }
            } catch (error) {
                console.error("Error checking attendance:", error);
                setError("Error checking attendance data.");
            }
        };
        checkAttendance();
    }, [userId]);

    // Fetch and update attendance data using MyKlassroom credentials
    const fetchAttendanceData = async () => {
        setLoading(true);
        setError("");

        try {
            // Fetch attendance data from Flask API
            const response = await axios.get(
                `http://127.0.0.1:5000/api/fetch-attendance?username=${username}&password=${password}`
            );

            if (response.status === 200) {
                const attendanceList = response.data.attendance_data.map(subject => ({
                    subjectName: subject.subject_name,
                    subjectCode: subject.subject_code,
                    totalHours: subject.total_hours,
                    hoursPresent: subject.hours_present,
                    hoursAbsent: subject.hours_absent,
                    attendancePercentage: subject.attendance_percentage
                }));

                // Save attendance data to Spring Boot backend
                await axios.put(
                    `http://localhost:8080/attendance/update/${userId}`, 
                    attendanceList
                );

                setAttendanceData(attendanceList);
                setIsDataAvailable(true);
            }
        } catch (error) {
            console.error("Error fetching attendance data:", error);
            setError("Error fetching attendance data.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <h2>My Attendance</h2>
            {isDataAvailable ? (
                <div>
                    <h3>Existing Attendance Data</h3>
                    <table>
                        <thead>
                            <tr>
                                <th>Subject Name</th>
                                <th>Subject Code</th>
                                <th>Total Hours</th>
                                <th>Hours Present</th>
                                <th>Hours Absent</th>
                                <th>Attendance Percentage</th>
                            </tr>
                        </thead>
                        <tbody>
                            {attendanceData && attendanceData.map((subject, index) => (
                                <tr key={index}>
                                    <td>{subject.subjectName}</td>
                                    <td>{subject.subjectCode}</td>
                                    <td>{subject.totalHours}</td>
                                    <td>{subject.hoursPresent}</td>
                                    <td>{subject.hoursAbsent}</td>
                                    <td>{subject.attendancePercentage}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    <h3>Update Attendance Data</h3>
                    <input
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button onClick={fetchAttendanceData} disabled={loading}>
                        {loading ? "Loading..." : "Fetch Attendance"}
                    </button>
                </div>
            ) : (
                <div>
                    <h3>Enter MyKlassroom Credentials</h3>
                    <input
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button onClick={fetchAttendanceData} disabled={loading}>
                        {loading ? "Loading..." : "Fetch Attendance"}
                    </button>
                </div>
            )}

            {error && <p style={{ color: "red" }}>{error}</p>}
        </div>
    );
};

export default Attendance;
