import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
    const [email, setEmail] = useState('');
    const [name, setName] = useState('');
    const [profilePicture, setProfilePicture] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            // Check if user exists
            const userExistsResponse = await axios.get(`http://localhost:8080/users/exists/${email}`);
            const userExists = userExistsResponse.data;

            if (userExists) {
                // User exists, fetch user data
                const userResponse = await axios.get(`http://localhost:8080/users/${email}`);
                const user = userResponse.data;
                // Navigate to chat history
                navigate('/chathistory');
            } else {
                // User does not exist, create new user
                const newUser = {
                    mailId: email,
                    name: name,
                    profilePictureLink: profilePicture
                };
                await axios.post('http://localhost:8080/users', newUser);
                // Navigate to chat history
                navigate('/chathistory');
            }
        } catch (error) {
            setError(error.message);
        }
    };

    return (
        <div>
            <h1>Login</h1>
            <form onSubmit={handleLogin}>
                <div>
                    <label>Name:</label>
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Profile Picture URL:</label>
                    <input
                        type="text"
                        value={profilePicture}
                        onChange={(e) => setProfilePicture(e.target.value)}
                    />
                </div>
                {error && <p style={{ color: 'red' }}>{error}</p>}
                <button type="submit">Login</button>
            </form>
        </div>
    );
};

export default Login;
