import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { auth, signInWithGoogle } from "../firebaseConfig";
import { onAuthStateChanged, signOut } from "firebase/auth";

const LoginComponent = () => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        // Listen for authentication state changes
        const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
            setUser(currentUser);
            setLoading(false); // Stop loading once authentication state is determined

            if (currentUser) {
                try {
                    const email = currentUser.email;
                    const response = await axios.get(`http://localhost:8080/api/users/check/${email}`);

                    if (response.data) {
                        navigate("/dashboard"); // Redirect existing user to dashboard
                    } else {
                        navigate("/register"); // Redirect new user to registration page
                    }
                } catch (error) {
                    console.error("Error checking user:", error);
                }
            }
        });

        return () => unsubscribe(); // Cleanup on component unmount
    }, [navigate]);

    const handleLogin = async () => {
        setLoading(true);
        const signedInUser = await signInWithGoogle();
        if (!signedInUser) {
            setLoading(false);
            return;
        }

        try {
            const email = signedInUser.email;
            const response = await axios.get(`http://localhost:8080/api/users/check/${email}`);

            if (response.data) {
                navigate("/dashboard"); // Existing user → Dashboard
            } else {
                navigate("/register"); // New user → Registration
            }
        } catch (error) {
            console.error("Error checking user:", error);
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = async () => {
        await signOut(auth);
        setUser(null);
    };

    return (
        <div style={{ textAlign: "center", marginTop: "50px" }}>
            <h2>Welcome to Our Platform</h2>

            {loading ? (
                <p>Loading...</p>
            ) : user ? (
                <>
                    <p>Logged in as {user.displayName}</p>
                    <button onClick={handleLogout}>Logout</button>
                </>
            ) : (
                <button onClick={handleLogin}>Sign in with Google</button>
            )}
        </div>
    );
};

export default LoginComponent;
