import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { UserContext } from '../Contexts/User';
import { Container, Typography, Box, Button, RadioGroup, FormControlLabel, Radio } from '@mui/material';

const IntroductoryQuiz = () => {
    const { courseId } = useParams();
    const [quiz, setQuiz] = useState(null);
    const [answers, setAnswers] = useState({});
    const { user } = useContext(UserContext);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchQuiz = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/courses/${courseId}/introductory-quiz`);
                setQuiz(response.data);
                // Initialize answers object
                const initialAnswers = {};
                response.data.questions.forEach(question => {
                    initialAnswers[question.id] = '';
                });
                setAnswers(initialAnswers);
            } catch (error) {
                console.error('Error fetching quiz:', error);
            }
        };

        fetchQuiz();
    }, [courseId]);

    const handleAnswerChange = (questionId, optionIndex) => {
        setAnswers(prevAnswers => ({
            ...prevAnswers,
            [questionId]: optionIndex
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!quiz || !user) {
            alert('Quiz or user data is missing');
            return;
        }

        // Check if all questions are answered
        const unansweredQuestions = quiz.questions.filter(question => answers[question.id] === '');
        if (unansweredQuestions.length > 0) {
            alert('Please answer all questions before submitting.');
            return;
        }

        try {
            // Transform answers into the new format expected by the backend
            const questionSummaries = quiz.questions.map(question => ({
                question: {
                    id: question.id
                },
                selectedOptionIndex: parseInt(answers[question.id])
            }));

            console.log('Submitting quiz with data:', JSON.stringify(questionSummaries, null, 2));

            // Use the new endpoint
            const response = await axios.post(
                `http://localhost:8080/users/${user.id}/courses/${courseId}/intro-quiz`, 
                questionSummaries,
                {
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    validateStatus: (status) => status < 500
                }
            );

            console.log('Response:', {
                status: response.status,
                headers: response.headers,
                data: response.data
            });

            if (response.status === 200 || response.status === 201) {
                if (response.data) {
                    const summaryId = response.data.id;
                    navigate(`/quiz-summary/${summaryId}`);
                } else {
                    throw new Error('Empty response received from server');
                }
            } else {
                throw new Error(`Server returned status ${response.status}: ${response.data}`);
            }
        } catch (error) {
            console.error('Error details:', {
                message: error.message,
                response: {
                    status: error.response?.status,
                    data: error.response?.data,
                    headers: error.response?.headers
                },
                request: error.config
            });

            const errorMessage = error.response?.data?.message || 
                               (typeof error.response?.data === 'string' ? error.response.data : null) || 
                               error.message;

            alert(`Failed to submit quiz: ${errorMessage}`);
        }
    };

    if (!quiz) {
        return <div>Loading...</div>;
    }

    return (
        <Container>
            <Box mt={4}>
                <Typography variant="h4" component="h1" gutterBottom>
                    {quiz.name}
                </Typography>
                <Typography variant="body1" gutterBottom color="textSecondary">
                    Please answer all questions to proceed with the course.
                </Typography>
                <form onSubmit={handleSubmit}>
                    {quiz.questions.map((question, questionIndex) => (
                        <Box key={question.id} mt={4} mb={4} p={2} border={1} borderRadius={1} borderColor="divider">
                            <Typography variant="h6" component="h2" gutterBottom>
                                {`${questionIndex + 1}. ${question.name}`}
                            </Typography>
                            <RadioGroup
                                value={answers[question.id]}
                                onChange={(e) => handleAnswerChange(question.id, e.target.value)}
                            >
                                {question.options.map((option, optionIndex) => (
                                    <FormControlLabel
                                        key={optionIndex}
                                        value={optionIndex}
                                        control={<Radio />}
                                        label={option.text}
                                    />
                                ))}
                            </RadioGroup>
                        </Box>
                    ))}
                    <Box mt={4} mb={4} display="flex" justifyContent="center">
                        <Button 
                            type="submit" 
                            variant="contained" 
                            color="primary" 
                            size="large"
                        >
                            Submit Quiz
                        </Button>
                    </Box>
                </form>
            </Box>
        </Container>
    );
};

export default IntroductoryQuiz;
