import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { UserContext } from '../Contexts/User';
import { Button, Typography, Box, Container, List, ListItem, ListItemText, ListItemIcon } from '@mui/material';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import RadioButtonUncheckedIcon from '@mui/icons-material/RadioButtonUnchecked';
import YouTube from 'react-youtube';

const Learn = () => {
    const { courseId, lessonId } = useParams();
    const [course, setCourse] = useState(null);
    const [lesson, setLesson] = useState(null);
    const [completedLessons, setCompletedLessons] = useState([]);
    const { user } = useContext(UserContext);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchCourse = async () => {
            try {
                const courseResponse = await axios.get(`http://localhost:8080/courses/${courseId}`);
                setCourse(courseResponse.data);

                const lessonResponse = await axios.get(`http://localhost:8080/courses/${courseId}/lessons/${lessonId}`);
                setLesson(lessonResponse.data);

                const completedLessonsResponse = await axios.get(`http://localhost:8080/users/${user.id}/courses/${courseId}/completed-lessons`);
                setCompletedLessons(completedLessonsResponse.data);
            } catch (error) {
                console.error('Error fetching course or lesson:', error);
            }
        };

        if (user) {
            fetchCourse();
        }
    }, [courseId, lessonId, user]);

    const handleQuiz = () => {
        navigate(`/quiz/${courseId}/${lessonId}`);
    };

    const handleLessonClick = (lessonId) => {
        navigate(`/learn/${courseId}/${lessonId}`);
    };

    if (!course || !lesson) {
        return <div>Loading...</div>;
    }

    return (
        <Container>
            <Typography variant="h4" component="h1" mt={4}>
                {course.name}
            </Typography>
            <Box display="flex" mt={4}>
                <Box flex={1}>
                    <YouTube videoId={lesson.youtubeVideoLink.split('v=')[1]} opts={{ width: '100%', height: '400px' }} />
                    <Box mt={2}>
                        <Button variant="contained" color="primary" onClick={handleQuiz}>
                            Attend Quiz
                        </Button>
                    </Box>
                    <Typography variant="h6" component="h2" mt={2}>
                        {lesson.name}
                    </Typography>
                    <Typography variant="body1" component="p" mt={2}>
                        {lesson.description}
                    </Typography>
                </Box>
                <Box ml={4} width="300px">
                    <Typography variant="h6" component="h2">
                        Lessons
                    </Typography>
                    <List>
                        {course.lessons.map((l) => (
                            <ListItem
                                button
                                key={l.id}
                                onClick={() => handleLessonClick(l.id)}
                                style={{
                                    backgroundColor: completedLessons.some(cl => cl.id === l.id) ? '#d3ffd3' : '#ffd3d3',
                                }}
                            >
                                <ListItemIcon>
                                    {completedLessons.some(cl => cl.id === l.id) ? (
                                        <CheckCircleIcon color="primary" />
                                    ) : (
                                        <RadioButtonUncheckedIcon color="secondary" />
                                    )}
                                </ListItemIcon>
                                <ListItemText primary={l.name} />
                            </ListItem>
                        ))}
                    </List>
                </Box>
            </Box>
        </Container>
    );
};

export default Learn;
