import { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";

const Register = () => {
    const location = useLocation();
    const navigate = useNavigate();

    // Retrieve GoogleAuthID, Email, and PhotoURL from LoginComponent (Hidden)
    const { googleAuthId, email, photoURL } = location.state || {};

    const [formData, setFormData] = useState({
        name: "",
        department: "",
        yearOfStudy: "",
        rollNo: "",
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const userData = {
            googleAuthId, // Send but don't show
            email, // Read-only
            photoURL, // Send but don't show
            ...formData
        };

        try {
            await axios.post("http://localhost:8080/api/users/register", userData);
            navigate("/dashboard"); // Redirect to dashboard after successful registration
        } catch (error) {
            console.error("Error registering user:", error);
        }
    };

    return (
        <div style={{ textAlign: "center", marginTop: "50px" }}>
            <h2>Complete Your Registration</h2>
            <form onSubmit={handleSubmit}>
                {/* Read-Only Email at the Top */}
                <div>
                    <label>Email:</label>
                    <input type="email" name="email" value={email} readOnly />
                </div>

                {/* User Inputs (Name, Department, Year of Study, Roll No) */}
                <div>
                    <label>Name:</label>
                    <input type="text" name="name" placeholder="Enter Name" onChange={handleChange} required />
                </div>

                <div>
                    <label>Department:</label>
                    <input type="text" name="department" placeholder="Enter Department" onChange={handleChange} required />
                </div>

                <div>
                    <label>Year of Study:</label>
                    <input type="number" name="yearOfStudy" placeholder="Enter Year of Study" onChange={handleChange} required />
                </div>

                <div>
                    <label>Roll No:</label>
                    <input type="text" name="rollNo" placeholder="Enter Roll No" onChange={handleChange} required />
                </div>

                <button type="submit">Register</button>
            </form>
        </div>
    );
};

export default Register;
