import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { UserContext } from '../Contexts/User';
import { Container, Typography, Box, Button } from '@mui/material';

const QuizSummary = () => {
    const { quizSummaryId } = useParams();
    const [quizSummary, setQuizSummary] = useState(null);
    const { user } = useContext(UserContext);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchQuizSummary = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/users/${user.id}/quiz-summary/${quizSummaryId}`);
                setQuizSummary(response.data);
            } catch (error) {
                console.error('Error fetching quiz summary:', error);
            }
        };

        fetchQuizSummary();
    }, [quizSummaryId, user]);

    const handleNextLesson = async () => {
        if (quizSummary.quiz.type === 'FINAL' && quizSummary.score >= 80) {
            await axios.post(`http://localhost:8080/users/${user.id}/complete/${quizSummary.course.id}`);
        }
        navigate(`/learn/${quizSummary.course.id}/${quizSummary.nextLessonId}`);
    };

    if (!quizSummary) {
        return <div>Loading...</div>;
    }

    return (
        <Container>
            <Box mt={4}>
                <Typography variant="h4" component="h1">
                    Quiz Summary
                </Typography>
                <Typography variant="h6" component="h2" mt={2}>
                    Score: {quizSummary.score}%
                </Typography>
                <Typography variant="body1" component="p" mt={2}>
                    {quizSummary.score >= 80 ? 'Congratulations! You passed the quiz.' : 'You did not pass the quiz.'}
                </Typography>
                <Box mt={4}>
                    <Button variant="contained" color="primary" onClick={handleNextLesson}>
                        {quizSummary.quiz.type === 'FINAL' ? 'Finish Course' : 'Next Lesson'}
                    </Button>
                </Box>
            </Box>
        </Container>
    );
};

export default QuizSummary;
