import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { UserContext } from '../Contexts/User';
import { Button, LinearProgress, Typography, Box, Container, List, ListItem, ListItemText } from '@mui/material';
import CourseDetails from '../Components/Course/CourseDetails';

const Course = () => {
    const { courseId } = useParams();
    const [course, setCourse] = useState(null);
    const [enrolled, setEnrolled] = useState(false);
    const [completed, setCompleted] = useState(false);
    const [completionPercentage, setCompletionPercentage] = useState(0);
    const { user } = useContext(UserContext);

    useEffect(() => {
        const fetchCourse = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/courses/${courseId}`);
                setCourse(response.data);
                if (user) {
                    const enrolledResponse = await axios.get(`http://localhost:8080/users/${user.id}/enrolled-courses`);
                    const enrolledCourses = enrolledResponse.data;
                    const isEnrolled = enrolledCourses.some(c => c.id === courseId);
                    setEnrolled(isEnrolled);

                    const completedResponse = await axios.get(`http://localhost:8080/users/${user.id}/completed-courses`);
                    const completedCourses = completedResponse.data;
                    const isCompleted = completedCourses.some(c => c.id === courseId);
                    setCompleted(isCompleted);

                    if (isEnrolled) {
                        const completionResponse = await axios.get(`http://localhost:8080/users/${user.id}/courses/${courseId}/completion-percentage`);
                        setCompletionPercentage(completionResponse.data);
                    }
                }
            } catch (error) {
                console.error('Error fetching course:', error);
            }
        };

        fetchCourse();
    }, [courseId, user]);

    const handleEnroll = async () => {
        try {
            await axios.post(`http://localhost:8080/users/${user.id}/enroll/${courseId}`);
            setEnrolled(true);
        } catch (error) {
            console.error('Error enrolling in course:', error);
        }
    };

    const handleDownloadCertificate = () => {
        // Implement certificate download logic here
        console.log('Download certificate');
    };

    if (!course) {
        return <div>Loading...</div>;
    }

    return (
        <Container>
            <Box display="flex" flexDirection="column" alignItems="center" mb={4}>
                <img src={course.imageUrl} alt={course.name} style={{ width: '100%', maxHeight: '300px', objectFit: 'cover' }} />
                <Typography variant="h4" component="h1" mt={2}>
                    {course.name}
                </Typography>
            </Box>
            <Box display="flex" justifyContent="space-between">
                <Box flex={1}>
                    <CourseDetails course={course} />
                    <Typography variant="h6" component="h2" mt={4}>
                        Course Description
                    </Typography>
                    <Typography variant="body1" component="p" mt={2}>
                        {course.description}
                    </Typography>
                    <Typography variant="h6" component="h2" mt={4}>
                        Lessons
                    </Typography>
                    <List>
                        {course.lessons.map((lesson) => (
                            <ListItem key={lesson.id}>
                                <ListItemText primary={lesson.name} secondary={lesson.description} />
                            </ListItem>
                        ))}
                    </List>
                </Box>
                <Box ml={4} width="200px">
                    {completed ? (
                        <Button variant="contained" color="primary" onClick={handleDownloadCertificate}>
                            Download Certificate
                        </Button>
                    ) : enrolled ? (
                        <Box>
                            <Typography variant="h6">Course Progress</Typography>
                            <LinearProgress variant="determinate" value={completionPercentage} />
                            <Typography variant="body2" color="textSecondary">{`${completionPercentage}%`}</Typography>
                            <Button variant="contained" color="primary" onClick={() => console.log('Start Learning')}>
                                Start Learning
                            </Button>
                        </Box>
                    ) : (
                        <Button variant="contained" color="primary" onClick={handleEnroll}>
                            Enroll
                        </Button>
                    )}
                </Box>
            </Box>
        </Container>
    );
};

export default Course;
