import React, { useContext, useEffect, useState } from 'react';
import { Container, Typography, Box } from '@mui/material';
import { UserContext } from '../Contexts/User';
import CourseList from '../Components/Course/CourseList';
import axios from 'axios';

const Homepage = () => {
    const { user } = useContext(UserContext);
    const [inProgressCourses, setInProgressCourses] = useState([]);
    const [recommendedCourses, setRecommendedCourses] = useState([]);
    const [allCourses, setAllCourses] = useState([]);

    useEffect(() => {
        const fetchInProgressCourses = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/users/${user.id}/enrolled-courses`);
                setInProgressCourses(response.data);
            } catch (error) {
                console.error('Error fetching in-progress courses:', error);
            }
        };

        const fetchRecommendedCourses = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/users/${user.id}/recommended-courses`);
                setRecommendedCourses(response.data);
            } catch (error) {
                console.error('Error fetching recommended courses:', error);
            }
        };

        const fetchAllCourses = async () => {
            try {
                const response = await axios.get('http://localhost:8080/courses');
                setAllCourses(response.data);
            } catch (error) {
                console.error('Error fetching all courses:', error);
            }
        };

        if (user) {
            fetchInProgressCourses();
            fetchRecommendedCourses();
            fetchAllCourses();
        }
    }, [user]);

    return (
        <Container>
            <Box mt={4}>
                <Typography variant="h4" component="h1">
                    Welcome, {user.name}!
                </Typography>
                <Typography variant="h6" component="h2" mt={2}>
                    Your In-Progress Courses
                </Typography>
                <CourseList courses={inProgressCourses} />
                <Typography variant="h6" component="h2" mt={4}>
                    Recommended Courses
                </Typography>
                <CourseList courses={recommendedCourses} />
                <Typography variant="h6" component="h2" mt={4}>
                    All Courses
                </Typography>
                <CourseList courses={allCourses} />
            </Box>
        </Container>
    );
};

export default Homepage;
