import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { UserContext } from '../Contexts/User';
import { 
    Container, 
    Typography, 
    Box, 
    Button, 
    List, 
    ListItem, 
    ListItemIcon, 
    ListItemText,
    Paper,
    CircularProgress
} from '@mui/material';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import CancelIcon from '@mui/icons-material/Cancel';

const QuizSummary = () => {
    const { quizId } = useParams();
    const [summary, setSummary] = useState(null);
    const [loading, setLoading] = useState(true);
    const { user } = useContext(UserContext);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchSummary = async () => {
            try {
                setLoading(true);
                const response = await axios.get(
                    `http://localhost:8080/users/${user.id}/quiz/${quizId}/results`
                );
                // Get the latest quiz attempt
                const latestSummary = response.data[response.data.length - 1];
                setSummary(latestSummary);
            } catch (error) {
                console.error('Error fetching quiz summary:', error);
            } finally {
                setLoading(false);
            }
        };

        if (user && quizId) {
            fetchSummary();
        }
    }, [quizId, user]);

    const getNextLessonId = () => {
        if (!summary?.quiz?.course?.lessons) return null;
        const lessons = summary.quiz.course.lessons;
        const currentLessonIndex = lessons.findIndex(lesson => lesson.quiz?.id === summary.quiz.id);
        return currentLessonIndex < lessons.length - 1 ? lessons[currentLessonIndex + 1].id : null;
    };

    const handleContinue = async () => {
        try {
            const course = summary.quiz.course;
            const passed = calculateScore() >= getPassingScore();

            if (passed) {
                if (isQuizType('FINAL')) {
                    await axios.post(`http://localhost:8080/users/${user.id}/complete/${course.id}`);
                    navigate('/certificates');
                } else {
                    const nextLessonId = getNextLessonId();
                    if (nextLessonId) {
                        navigate(`/learn/${course.id}/${nextLessonId}`);
                    } else {
                        // If no next lesson, go back to course page
                        navigate(`/courses/${course.id}`);
                    }
                }
            } else {
                navigate(`/courses/${course.id}`);
            }
        } catch (error) {
            console.error('Error handling navigation:', error);
        }
    };

    const calculateScore = () => {
        if (!summary?.questionSummaries?.length) return 0;
        const correctAnswers = summary.questionSummaries.filter(qs => qs.isCorrect).length;
        return Math.round((correctAnswers / summary.questionSummaries.length) * 100);
    };

    const getPassingScore = () => {
        return isQuizType('FINAL') ? 80 : 60;
    };

    const isQuizType = (type) => {
        return summary.quiz.type === type;
    };

    if (loading) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
                <CircularProgress />
            </Box>
        );
    }

    if (!summary) {
        return (
            <Container>
                <Typography variant="h6" color="error">
                    Failed to load quiz summary. Please try again.
                </Typography>
            </Container>
        );
    }

    const score = calculateScore();
    const passed = score >= getPassingScore();

    return (
        <Container maxWidth="md">
            <Box mt={4} mb={4}>
                <Paper elevation={3} sx={{ p: 4 }}>
                    <Typography variant="h4" align="center" gutterBottom>
                        {summary.quiz.name} Results
                    </Typography>

                    <Box mt={4} textAlign="center">
                        <Typography variant="h1" 
                            color={passed ? 'success.main' : 'error.main'}
                            sx={{ mb: 2 }}
                        >
                            {score}%
                        </Typography>
                        <Typography variant="h5" 
                            color={passed ? 'success.main' : 'error.main'}
                            sx={{ mb: 3 }}
                        >
                            {passed ? 'Congratulations! You Passed!' : 'Please Try Again'}
                        </Typography>
                    </Box>

                    <List sx={{ mt: 4 }}>
                        {summary.questionSummaries.map((qs, index) => (
                            <ListItem key={index} sx={{ mb: 2, bgcolor: 'background.paper', borderRadius: 1 }}>
                                <ListItemIcon>
                                    {qs.isCorrect ? 
                                        <CheckCircleIcon color="success" /> : 
                                        <CancelIcon color="error" />
                                    }
                                </ListItemIcon>
                                <ListItemText
                                    primary={qs.question.name}
                                    secondary={
                                        <>
                                            <Typography component="span" color="textSecondary">
                                                Your answer: {qs.selectedOption?.text}
                                            </Typography>
                                            {!qs.isCorrect && (
                                                <Typography component="span" color="error" sx={{ display: 'block' }}>
                                                    Correct answer: {qs.question.options.find(opt => opt.isCorrect)?.text}
                                                </Typography>
                                            )}
                                        </>
                                    }
                                />
                            </ListItem>
                        ))}
                    </List>

                    <Box mt={4} textAlign="center">
                        <Button
                            variant="contained"
                            color="primary"
                            size="large"
                            onClick={handleContinue}
                        >
                            {passed ? 
                                (isQuizType('FINAL') ? 'View Certificate' : 'Continue to Next Lesson') : 
                                'Return to Course'}
                        </Button>
                    </Box>
                </Paper>
            </Box>
        </Container>
    );
};

export default QuizSummary;
