import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { CircularProgress } from "@mui/material";
import { auth } from "./firebase";
import DashboardComponent from "./components/Dashboard";
import LoginComponent from "./components/Login";
import Register from "./components/Registration";
import Attendance from "./components/Attendance";
import axios from "axios";

const App = () => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [isNewUser, setIsNewUser] = useState(false);

    useEffect(() => {
        const unsubscribe = auth.onAuthStateChanged(async (currentUser) => {
            setUser(currentUser);

            if (currentUser) {
                try {
                    const response = await axios.get(`http://localhost:8080/api/users/check/${currentUser.email}`);

                    if (!response.data) {
                        setIsNewUser(true);
                    } else {
                        setIsNewUser(false);
                    }
                } catch (error) {
                    console.error("Error checking user existence:", error);
                }
            }
            setLoading(false);
        });

        return () => unsubscribe();
    }, []);

    const PrivateRoute = ({ children }) => {
        if (!user) return <Navigate to="/login" />;
        if (isNewUser) return <Navigate to="/register" />;
        return children;
    };

    return (
        <Router>
            {loading ? (
                <CircularProgress />
            ) : (
                <Routes>
                    <Route path="/login" element={<LoginComponent />} />
                    <Route path="/register" element={<Register />} />
                    <Route path="/dashboard" element={<DashboardComponent />} />
                    <Route path="/attendance" element={<PrivateRoute><Attendance email={user?.email} /></PrivateRoute>} />
                    <Route path="/" element={user ? <Navigate to="/attendance" /> : <Navigate to="/login" />} />
                </Routes>
            )}
        </Router>
    );
};

export default App;
