import React, { useContext, useEffect, useState } from 'react';
import { Container, Typography, Box } from '@mui/material';
import { UserContext } from '../Contexts/User';
import CourseList from '../Components/Course/CourseList';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Certificates = () => {
    const { user } = useContext(UserContext);
    const [completedCourses, setCompletedCourses] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchCompletedCourses = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/users/${user.id}/completed-courses`);
                setCompletedCourses(response.data);
            } catch (error) {
                console.error('Error fetching completed courses:', error);
            }
        };

        if (user) {
            fetchCompletedCourses();
        }
    }, [user]);

    const handleCourseClick = (courseId) => {
        navigate(`/courses/${courseId}`);
    };

    return (
        <Container>
            <Box mt={4}>
                <Typography variant="h4" component="h1">
                    Completed Courses
                </Typography>
                <Typography variant="h6" component="h2" mt={2}>
                    Your Certificates
                </Typography>
                <Box onClick={() => handleCourseClick(course.id)} style={{ cursor: 'pointer' }}>
                    <CourseList courses={completedCourses} />
                </Box>
            </Box>
        </Container>
    );
};

export default Certificates;
