import { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";

const Register = () => {
    const location = useLocation();
    const navigate = useNavigate();

    // Retrieve GoogleAuthID, Email, and PhotoURL from LoginComponent (Hidden)
    const { googleAuthId, email, photoURL } = location.state || {};

    const [formData, setFormData] = useState({
        name: "",
        department: "",
        yearOfStudy: "",
    });

    const [loading, setLoading] = useState(false);  // Loading state
    const [error, setError] = useState("");  // To display error message

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);  // Start loading
        setError("");  // Clear any previous errors

        const userData = {
            email, // Read-only
            photoUrl: photoURL, // Send but don't show
            ...formData
        };

        try {
            // Send registration data to backend
            const response = await axios.post("http://localhost:8080/users/add", userData);

            if (response.status === 200) {
                navigate("/attendance"); // Redirect to dashboard after successful registration
            } else {
                setError("Registration failed. Please try again."); // Handle failure case
            }
        } catch (error) {
            console.error("Error registering user:", error);
            setError("Registration failed. Please try again.");
        } finally {
            setLoading(false);  // End loading
        }
    };

    return (
        <div style={{ textAlign: "center", marginTop: "50px" }}>
            <h2>Complete Your Registration</h2>

            {/* Display error message */}
            {error && <p style={{ color: "red" }}>{error}</p>}

            <form onSubmit={handleSubmit}>
                {/* Read-Only Email at the Top */}
                <div>
                    <label>Email:</label>
                    <input type="email" name="email" value={email} readOnly />
                </div>

                {/* User Inputs (Name, Department, Year of Study) */}
                <div>
                    <label>Name:</label>
                    <input type="text" name="name" placeholder="Enter Name" onChange={handleChange} required />
                </div>

                <div>
                    <label>Department:</label>
                    <input type="text" name="department" placeholder="Enter Department" onChange={handleChange} required />
                </div>

                <div>
                    <label>Year of Study:</label>
                    <input type="number" name="yearOfStudy" placeholder="Enter Year of Study" onChange={handleChange} required />
                </div>

                <button type="submit" disabled={loading}> {loading ? "Registering..." : "Register"} </button>
            </form>
        </div>
    );
};

export default Register;
