import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Chat = () => {
    const { chatId } = useParams();
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');

    useEffect(() => {
        // Fetch chat messages when the component mounts
        axios.get(`http://localhost:8080/chats/${chatId}`)
            .then(response => {
                setMessages(response.data.messages);
            })
            .catch(error => {
                console.error('Error fetching chat messages:', error);
            });
    }, [chatId]);

    const handleSend = () => {
        if (!input.trim()) return;

        const userMessage = {
            sender: 'user',
            content: input,
            timestamp: new Date().toISOString()
        };

        // Add user message to the chat
        axios.put(`http://localhost:8080/chats/${chatId}/messages`, userMessage)
            .then(response => {
                setMessages([...messages, userMessage]);

                // Send user input to the Flask backend API
                axios.post('http://127.0.0.1:5000/chat', { message: input })
                    .then(apiResponse => {
                        const aiMessage = {
                            sender: 'ai',
                            content: apiResponse.data.response,
                            timestamp: new Date().toISOString()
                        };

                        // Add AI message to the chat
                        axios.put(`http://localhost:8080/chats/${chatId}/messages`, aiMessage)
                            .then(() => {
                                setMessages(prevMessages => [...prevMessages, aiMessage]);
                            })
                            .catch(error => {
                                console.error('Error adding AI message to chat:', error);
                            });
                    })
                    .catch(error => {
                        console.error('Error getting AI response:', error);
                    });
            })
            .catch(error => {
                console.error('Error adding user message to chat:', error);
            });

        setInput('');
    };

    const formatMessageContent = (content) => {
        const formattedContent = content.replace(/\**\*(.*?)\**|/g, '<strong>$1</strong>');
        return { __html: formattedContent };
    };

    return (
        <div>
            <h1>Chat</h1>
            <div className="chat-history">
                {messages.map((message, index) => (
                    <div key={index} className={`message ${message.sender}`}>
                        <span className="timestamp">{new Date(message.timestamp).toLocaleTimeString()}</span>
                        <span className="content" style={{ whiteSpace: 'pre-wrap' }} dangerouslySetInnerHTML={formatMessageContent(message.content)}></span>
                    </div>
                ))}
            </div>
            <div className="chat-input">
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    placeholder="Type your message..."
                />
                <button onClick={handleSend}>Send</button>
            </div>
        </div>
    );
};

export default Chat;
