import React, { useState } from 'react';
import { useNavigate, Navigate } from 'react-router-dom';
import { signInWithPopup } from 'firebase/auth';
import { auth, provider } from '../firebase';
import axios from 'axios';
import useAuth from '../hooks/useAuth';

const Login = () => {
    const [error, setError] = useState('');
    const navigate = useNavigate();
    const isAuthenticated = useAuth();

    const handleGoogleLogin = async () => {
        try {
            const result = await signInWithPopup(auth, provider);
            const user = result.user;
            const { displayName, email, photoURL } = user;

            // Check if user exists
            const userExistsResponse = await axios.get(`http://localhost:8080/users/exists/${email}`);
            const userExists = userExistsResponse.data;

            if (!userExists) {
                // User does not exist, create new user
                const newUser = {
                    mailId: email,
                    name: displayName,
                    profilePictureLink: photoURL
                };
                await axios.post('http://localhost:8080/users', newUser);
            }

            // Navigate to chat history with user email
            navigate('/chathistory', { state: { email } });
        } catch (error) {
            setError(error.message);
        }
    };

    if (isAuthenticated) {
        return <Navigate to="/" />;
    }

    return (
        <div>
            <h1>Login</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <button onClick={handleGoogleLogin}>Login with Google</button>
        </div>
    );
};

export default Login;
