import React, { useEffect, useState } from 'react';
import { Button, CircularProgress, Typography } from '@mui/material';
import { auth, googleProvider, signInWithPopup } from '../firebase'; // Firebase configuration file
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Axios for making HTTP requests


const LoginComponent = () => {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [user, setUserData] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        // Check if user is already logged in when component mounts
        const checkUser = auth.currentUser;
        if (checkUser) {
            navigate('/dashboard'); // Redirect to dashboard if already logged in
        }
    }, [navigate]);

    const handleLogin = async () => {
        setLoading(true); // Show loading spinner
        try {
            // Trigger the Google login popup
            const result = await signInWithPopup(auth, googleProvider); // Firebase authentication
            const user = result.user;

            // Prepare user data for backend (Spring Boot)
            const userData = {
                googleAuthId: user.uid,
                name: user.displayName,
                email: user.email,
                photoURL: user.photoURL,
            };

            // Send user data to the backend (Spring Boot) to create or fetch user
            const response = await axios.post('http://localhost:18/api/user/signup', userData);

            if (response.status === 200) {
                // Store user data in local storage or context
                setUserData(response.data); // Function to save user data (e.g., in local storage or context)

                // Redirect to dashboard after successful login and user creation
                navigate('/dashboard');
            } else {
                setError('Failed to create user. Please try again.');
            }
        } catch (error) {
            // Handle Firebase errors (like popup being blocked or network issues)
            console.error('Login error:', error);
            if (error.code === 'auth/popup-closed-by-user') {
                setError('Login popup was closed. Please try again.');
            } else if (error.response) {
                // Handle backend errors if any (e.g., 404 or 500)
                setError(`Backend error: ${error.response.data.message}`);
            } else {
                setError('Failed to log in. Please try again.');
            }
        } finally {
            setLoading(false); // Hide loading spinner
        }
    };

    return (
        <div style={{ textAlign: 'center', marginTop: '50px' }}>
            <Typography variant="h4">Login to Your Account</Typography>
            <div style={{ marginTop: '20px' }}>
                <Button
                    variant="contained"
                    color="primary"
                    onClick={handleLogin}
                    disabled={loading}
                    style={{ padding: '10px 30px' }}
                >
                    {loading ? (
                        <CircularProgress size={24} style={{ color: 'white' }} />
                    ) : (
                        'Login with Google'
                    )}
                </Button>
            </div>
            {error && (
                <Typography color="error" style={{ marginTop: '20px' }}>
                    {error}
                </Typography>
            )}
        </div>
    );
};

export default LoginComponent;
