import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { CircularProgress } from '@mui/material';
import { auth } from './firebase'; // Firebase authentication
import DashboardComponent from './components/DashboardComponent';
import LoginComponent from './components/LoginComponent';
import ProfileView from './components/ProfileView';
import TimetableManagement from './components/TimetableManagement';
import AdminConfigForm from './components/AdminConfigForm';

const App = () => {
    const [user, setUser] = useState(null);  // Track logged-in user
    const [loading, setLoading] = useState(true);  // Loading state while checking user authentication

    useEffect(() => {
        // Check if the user is authenticated with Firebase
        const unsubscribe = auth.onAuthStateChanged((user) => {
            setUser(user);  // Update user state
            setLoading(false);  // Set loading to false once user data is fetched
        });

        // Clean up the subscription on component unmount
        return () => unsubscribe();
    }, []);

    // Private Route for authenticated users
    const PrivateRoute = ({ children }) => {
        return user ? children : <Navigate to="/login" />;
    };

    return (
        <Router>
            {loading ? (
                <CircularProgress />
            ) : (
                <Routes>
                    {/* Route for Login */}
                    <Route path="/login" element={<LoginComponent />} />

                    {/* Private route for Dashboard */}
                    <Route
                        path="/dashboard"
                        element={
                            <PrivateRoute>
                                <DashboardComponent />
                            </PrivateRoute>
                        }
                    />

                    {/* Private route for Profile */}
                    <Route
                        path="/profile"
                        element={
                            <PrivateRoute>
                                <ProfileView />
                            </PrivateRoute>
                        }
                    />

                    {/* Private route for Timetable Management */}
                    <Route
                        path="/timetable"
                        element={
                            <PrivateRoute>
                                <TimetableManagement />
                            </PrivateRoute>
                        }
                    />

                    {/* Admin Route for Admin Config */}
                    <Route
                        path="/admin"
                        element={
                            <PrivateRoute>
                                {/* Admin should have access to this route */}
                                <AdminConfigForm />
                            </PrivateRoute>
                        }
                    />

                    {/* Redirect to Dashboard if already authenticated */}
                    <Route path="/" element={<Navigate to="/dashboard" />} />
                </Routes>
            )}
        </Router>
    );
};

export default App;
