import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Menubar = () => {
    const navigate = useNavigate();

    const handleSearch = (e) => {
        e.preventDefault();
        const query = e.target.elements.search.value;
        navigate(`/search?query=${query}`);
    };

    return (
        <nav style={{ display: 'flex', justifyContent: 'space-between', padding: '10px', background: '#f8f9fa' }}>
            <div>
                <Link to="/Homepage" style={{ margin: '0 10px' }}>Homepage</Link>
                <Link to="/enrolled-courses" style={{ margin: '0 10px' }}>Enrolled Courses</Link>
                <Link to="/certificates" style={{ margin: '0 10px' }}>Certificates</Link>
                <Link to="/profile" style={{ margin: '0 10px' }}>Profile</Link>
            </div>
            <form onSubmit={handleSearch} style={{ display: 'flex' }}>
                <input
                    type="text"
                    name="search"
                    placeholder="Search..."
                    style={{ padding: '5px' }}
                    onFocus={() => navigate('/search')}
                />
                <button type="submit" style={{ padding: '5px' }}>Search</button>
            </form>
        </nav>
    );
};

export default Menubar;
