import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { auth, signInWithGoogle } from "../firebaseConfig"; // Import Firebase functions
import { onAuthStateChanged, signOut } from "firebase/auth";

const LoginComponent = () => {
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        onAuthStateChanged(auth, async (user) => {
            if (user) {
                setLoading(true);
                try {
                    const email = user.email;
                    const response = await axios.get(`http://localhost:8080/api/users/check/${email}`);
                    if (response.data) {
                        navigate("/dashboard"); // Redirect existing user to dashboard
                    } else {
                        navigate("/register"); // Redirect new user to registration page
                    }
                } catch (error) {
                    console.error("Error checking user:", error);
                } finally {
                    setLoading(false);
                }
            }
        });
    }, [navigate]);

    const handleLogin = async () => {
        setLoading(true);
        const user = await signInWithGoogle();
        if (!user) {
            setLoading(false);
            return;
        }

        try {
            const email = user.email;
            const response = await axios.get(`http://localhost:8080/api/users/check/${email}`);

            if (response.data) {
                navigate("/dashboard"); // Existing user → Dashboard
            } else {
                navigate("/register"); // New user → Registration
            }
        } catch (error) {
            console.error("Error checking user:", error);
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = async () => {
        await signOut(auth);
        navigate("/"); // Redirect to home page
    };

    return (
        <div style={{ textAlign: "center", marginTop: "50px" }}>
            <h2>Welcome to Our Platform</h2>
            {loading ? <p>Loading...</p> : <button onClick={handleLogin}>Sign in with Google</button>}
            <button onClick={handleLogout} style={{ marginLeft: "10px" }}>Logout</button>
        </div>
    );
};

export default LoginComponent;
