import React, { useState, useEffect } from "react";
import axios from "axios";

const TimetableManagement = ({ email }) => {
    const [timetableData, setTimetableData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");
    const [dayOrder, setDayOrder] = useState(1);
    const [subjects, setSubjects] = useState([]);
    const [numHours, setNumHours] = useState(1);
    const [selectedSubjects, setSelectedSubjects] = useState([]);
    const [editMode, setEditMode] = useState(false);
    const [editId, setEditId] = useState(null);

    useEffect(() => {
        fetchTimetableData();
        fetchSubjects();
    }, [email]);

    const fetchTimetableData = async () => {
        setLoading(true);
        setError("");
        try {
            const response = await axios.get(`http://localhost:8080/timetable/${email}`);
            setTimetableData(response.data);
        } catch (error) {
            console.error("Error fetching timetable data:", error);
            setError("Error fetching timetable data.");
        } finally {
            setLoading(false);
        }
    };

    const fetchSubjects = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/attendance/subjects/${email}`);
            console.log("Fetched subjects:", response.data); // Debug log
            setSubjects(response.data);
        } catch (error) {
            console.error("Error fetching subjects:", error);
            setError("Error fetching subjects.");
        }
    };

    const handleAddOrEditTimetable = async () => {
        // Validate that all subject fields are filled
        if (selectedSubjects.some(subject => subject === "")) {
            setError("All subject fields must be filled.");
            return;
        }

        // Check if a record for the selected day order already exists
        if (!editMode && timetableData.some(timetable => timetable.dayOrder === dayOrder)) {
            setError("A record for this day order already exists.");
            return;
        }

        setLoading(true);
        setError("");
        try {
            if (editMode) {
                await axios.put(`http://localhost:8080/timetable/edit/${editId}`, { dayOrder, subjects: selectedSubjects });
            } else {
                await axios.post(`http://localhost:8080/timetable/add`, {
                    email,
                    dayOrder,
                    subjects: selectedSubjects
                });
            }
            fetchTimetableData();
            setEditMode(false);
            setEditId(null);
            setSelectedSubjects([]);
            setNumHours(1);
        } catch (error) {
            console.error("Error adding or editing timetable data:", error);
            setError("Error adding or editing timetable data.");
        } finally {
            setLoading(false);
        }
    };

    const handleEditClick = (id, dayOrder, subjects) => {
        setEditMode(true);
        setEditId(id);
        setDayOrder(dayOrder);
        setSelectedSubjects(subjects);
        setNumHours(subjects.length);
    };

    const handleCancelEdit = () => {
        setEditMode(false);
        setEditId(null);
        setSelectedSubjects([]);
        setNumHours(1);
    };

    const handleSubjectChange = (index, value) => {
        const newSelectedSubjects = [...selectedSubjects];
        newSelectedSubjects[index] = value;
        setSelectedSubjects(newSelectedSubjects);
    };

    const handleNumHoursChange = (e) => {
        const hours = parseInt(e.target.value);
        setNumHours(hours);
        setSelectedSubjects(Array(hours).fill(""));
    };

    return (
        <div>
            <h2>Timetable Management</h2>
            {loading && <p>Loading...</p>}
            {error && <p style={{ color: "red" }}>{error}</p>}
            <div>
                <h3>{editMode ? "Edit Timetable" : "Add Timetable"}</h3>
                <label>
                    Day Order:
                    <select value={dayOrder} onChange={(e) => setDayOrder(parseInt(e.target.value))}>
                        {[1, 2, 3, 4, 5].map(order => (
                            <option key={order} value={order}>{order}</option>
                        ))}
                    </select>
                </label>
                <br />
                <label>
                    Number of Hours:
                    <input
                        type="number"
                        min="1"
                        value={numHours}
                        onChange={handleNumHoursChange}
                    />
                </label>
                <br />
                {Array.from({ length: numHours }).map((_, index) => (
                    <label key={index}>
                        Subject for Hour {index + 1}:
                        <select value={selectedSubjects[index]} onChange={(e) => handleSubjectChange(index, e.target.value)}>
                            <option value="">Select Subject</option>
                            {subjects.map(subject => (
                                <option key={subject} value={subject}>{subject}</option>
                            ))}
                        </select>
                        <br />
                    </label>
                ))}
                <button onClick={handleAddOrEditTimetable} disabled={loading}>
                    {loading ? "Saving..." : editMode ? "Update Timetable" : "Add Timetable"}
                </button>
                {editMode && <button onClick={handleCancelEdit}>Cancel</button>}
            </div>
            <h3>Existing Timetable</h3>
            <table>
                <thead>
                    <tr>
                        <th>Day Order</th>
                        <th>Subjects</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {timetableData.map((timetable) => (
                        <tr key={timetable.id}>
                            <td>{timetable.dayOrder}</td>
                            <td>{timetable.subjects.join(", ")}</td>
                            <td>
                                <button onClick={() => handleEditClick(timetable.id, timetable.dayOrder, timetable.subjects)}>
                                    Edit
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default TimetableManagement;
