import React, { useState, useEffect } from 'react';
import { List, ListItem, Typography, Box, CircularProgress } from '@mui/material';
import axios from 'axios';
import { auth } from '../firebase';  // Firebase authentication

const AttendanceList = () => {
    const [attendance, setAttendance] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchAttendance = async () => {
            const user = auth.currentUser;
            if (user) {
                try {
                    // Fetch attendance data from the backend
                    const response = await axios.get(`/api/attendance/${user.uid}`);
                    setAttendance(response.data);
                } catch (error) {
                    console.error("Error fetching attendance:", error);
                }
            }
            setLoading(false);
        };

        fetchAttendance();
    }, []);

    return (
        <Box sx={{ padding: 2 }}>
            <Typography variant="h6" gutterBottom>Attendance Summary</Typography>
            {loading ? (
                <CircularProgress />
            ) : (
                <List>
                    {attendance.length === 0 ? (
                        <Typography>No attendance data available</Typography>
                    ) : (
                        attendance.map((item, index) => (
                            <ListItem key={index}>
                                <Typography variant="body1">
                                    {item.subjectName} - {item.attendancePercentage}% attendance
                                </Typography>
                            </ListItem>
                        ))
                    )}
                </List>
            )}
        </Box>
    );
};

export default AttendanceList;
