import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import { auth } from '../firebase';

const ChatHistory = () => {
    const [chats, setChats] = useState([]);
    const [user, setUser] = useState(null);
    const navigate = useNavigate();
    const location = useLocation();
    const email = location.state?.email || auth.currentUser?.email;

    useEffect(() => {
        if (email) {
            // Fetch user-specific chats when the component mounts
            axios.get(`http://localhost:8080/chats/user/${email}`)
                .then(response => {
                    setChats(response.data);
                })
                .catch(error => {
                    console.error('Error fetching chats:', error);
                });

            // Fetch user information
            axios.get(`http://localhost:8080/users/${email}`)
                .then(response => {
                    setUser(response.data);
                })
                .catch(error => {
                    console.error('Error fetching user information:', error);
                });
        }
    }, [email]);

    const handleChatClick = (chatId) => {
        // Navigate to the chat page with the selected chat ID and user email
        navigate(`/chat/${chatId}`, { state: { email } });
    };

    const handleCreateChat = () => {
        // Create a new chat with null data
        const newChat = {
            name: null,
            messages: []
        };

        axios.post(`http://localhost:8080/chats/${email}`, newChat)
            .then(response => {
                const newChatId = response.data.id;
                navigate(`/chat/${newChatId}`, { state: { email } });
            })
            .catch(error => {
                console.error('Error creating new chat:', error);
            });
    };

    const handleLogout = () => {
        auth.signOut().then(() => {
            navigate('/login');
        });
    };

    return (
        <div>
            <div className="header">
                {user && (
                    <>
                        <img src={user.profilePictureLink} alt="Profile" className="profile-picture" />
                        <span className="user-name">{user.name}</span>
                    </>
                )}
                <button onClick={handleLogout} className="logout-button">Logout</button>
            </div>
            <h1>Chat History</h1>
            <button onClick={handleCreateChat}>+ New Chat</button>
            <ul>
                {chats.map(chat => (
                    <li key={chat.id} onClick={() => handleChatClick(chat.id)}>
                        {chat.name || 'Unnamed Chat'}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ChatHistory;
