import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { auth } from "../firebase";

const Register = () => {
    const [formData, setFormData] = useState({
        name: "",
        department: "",
        yearOfStudy: "",
        rollNo: ""
    });

    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const user = auth.currentUser;
        if (user) {
            const userData = {
                googleAuthId: user.uid,
                name: formData.name,
                email: user.email,
                photoURL: user.photoURL,
                department: formData.department,
                yearOfStudy: formData.yearOfStudy,
                rollNo: formData.rollNo
            };

            try {
                await axios.post("http://localhost:8080/api/users/register", userData);
                navigate("/dashboard");
            } catch (error) {
                console.error("Error registering user:", error);
            }
        }
    };

    return (
        <form onSubmit={handleSubmit} style={{ textAlign: "center", marginTop: "50px" }}>
            <input type="text" name="name" placeholder="Name" onChange={handleChange} required />
            <input type="text" name="department" placeholder="Department" onChange={handleChange} required />
            <input type="number" name="yearOfStudy" placeholder="Year of Study" onChange={handleChange} required />
            <input type="text" name="rollNo" placeholder="Roll No" onChange={handleChange} required />
            <button type="submit">Register</button>
        </form>
    );
};

export default Register;
