import React, { useState, useEffect } from 'react';
import { Button, TextField, Grid, FormControl, InputLabel, Select, MenuItem, Typography, Box } from '@mui/material';
import axios from 'axios';
import { auth } from '../firebase'; // Firebase authentication
import { useNavigate } from 'react-router-dom';

const TimetableManagement = () => {
    const [hoursPerDay, setHoursPerDay] = useState(5);  // Default to 5 hours per day
    const [subjects, setSubjects] = useState({});  // Store subjects for each hour
    const [currentTimetable, setCurrentTimetable] = useState(null);  // Store the fetched timetable
    const [dayOrder, setDayOrder] = useState(1);  // Default to Monday (day 1)
    const navigate = useNavigate();

    // Fetch existing timetable for the user when component mounts
    useEffect(() => {
        const fetchTimetable = async () => {
            const user = auth.currentUser;
            if (user) {
                try {
                    const response = await axios.get(`/api/timetable/${user.uid}/${dayOrder}`);
                    if (response.data.length > 0) {
                        setCurrentTimetable(response.data[0]);  // Assuming one timetable per day
                        setSubjects(response.data[0].subjects);  // Set subjects if data exists
                    }
                } catch (error) {
                    console.error("Error fetching timetable:", error);
                }
            }
        };

        fetchTimetable();
    }, [dayOrder]);

    // Handle saving or updating the timetable
    const handleSaveTimetable = async () => {
        const user = auth.currentUser;
        if (user) {
            try {
                await axios.post(`/api/timetable/`, {
                    googleAuthId: user.uid,
                    dayOrder,
                    subjects
                });
                alert('Timetable saved successfully!');
            } catch (error) {
                console.error('Error saving timetable:', error);
            }
        }
    };

    // Handle changes in subject input
    const handleChangeSubject = (hour, subject) => {
        setSubjects(prev => ({
            ...prev,
            [hour]: subject
        }));
    };

    // Handle number of hours per day change
    const handleChangeHours = (event) => {
        setHoursPerDay(event.target.value);
    };

    return (
        <Box sx={{ padding: 2 }}>
            <Typography variant="h6" gutterBottom>Timetable Management</Typography>

            {/* Select number of hours per day */}
            <FormControl fullWidth>
                <InputLabel>Hours per Day</InputLabel>
                <Select
                    value={hoursPerDay}
                    onChange={handleChangeHours}
                    label="Hours per Day"
                >
                    {[...Array(6).keys()].map(i => (
                        <MenuItem value={i + 1} key={i + 1}>{i + 1} hour(s)</MenuItem>
                    ))}
                </Select>
            </FormControl>

            {/* Display input fields for subjects based on hours per day */}
            <Grid container spacing={2} sx={{ marginTop: 2 }}>
                {[...Array(hoursPerDay).keys()].map(hourIndex => (
                    <Grid item xs={12} sm={2} key={hourIndex}>
                        <TextField
                            label={`Hour ${hourIndex + 1}`}
                            variant="outlined"
                            fullWidth
                            value={subjects[hourIndex + 1] || ''}
                            onChange={(e) => handleChangeSubject(hourIndex + 1, e.target.value)}
                        />
                    </Grid>
                ))}
            </Grid>

            {/* Buttons for saving and viewing timetable */}
            <Box sx={{ marginTop: 2 }}>
                <Button onClick={handleSaveTimetable} variant="contained">Save Timetable</Button>
            </Box>

            {/* Display the current timetable (if fetched from the backend) */}
            {currentTimetable && (
                <Box sx={{ marginTop: 3 }}>
                    <Typography variant="h6">Current Timetable for Day {dayOrder}</Typography>
                    <Grid container spacing={2}>
                        {[...Array(hoursPerDay).keys()].map(hourIndex => (
                            <Grid item xs={12} sm={2} key={hourIndex}>
                                <Typography>{`Hour ${hourIndex + 1}: ${currentTimetable.subjects[hourIndex + 1] || 'N/A'}`}</Typography>
                            </Grid>
                        ))}
                    </Grid>
                </Box>
            )}

            {/* Buttons to switch between days */}
            <Box sx={{ marginTop: 2 }}>
                <Button
                    variant="contained"
                    onClick={() => setDayOrder((prev) => prev > 1 ? prev - 1 : 1)}
                    sx={{ marginRight: 2 }}
                >
                    Previous Day
                </Button>
                <Button
                    variant="contained"
                    onClick={() => setDayOrder((prev) => prev < 5 ? prev + 1 : 5)}
                >
                    Next Day
                </Button>
            </Box>
        </Box>
    );
};

export default TimetableManagement;
