import React, { useState, useEffect } from 'react';
import { Button, TextField, Grid, Typography, Box, CircularProgress } from '@mui/material';
import axios from 'axios';
import { auth } from '../firebase'; // Firebase authentication

const TimetableForm = () => {
    const [hoursPerDay, setHoursPerDay] = useState(5); // Default: 5 hours per day
    const [subjects, setSubjects] = useState({}); // Store subjects for each hour
    const [loading, setLoading] = useState(false); // Loading state for saving timetable
    const [currentTimetable, setCurrentTimetable] = useState(null); // Store fetched timetable data

    // Fetch existing timetable when component mounts
    useEffect(() => {
        const fetchTimetable = async () => {
            const user = auth.currentUser;
            if (user) {
                try {
                    const response = await axios.get(`/api/timetable/${user.uid}`);
                    if (response.data.length > 0) {
                        setCurrentTimetable(response.data[0]);
                        setSubjects(response.data[0].subjects || {});
                    }
                } catch (error) {
                    console.error('Error fetching timetable:', error);
                }
            }
        };

        fetchTimetable();
    }, []);

    const handleSaveTimetable = async () => {
        const user = auth.currentUser;
        if (user) {
            setLoading(true);
            try {
                // Sending timetable data to backend
                await axios.post(`/api/timetable/`, {
                    googleAuthId: user.uid,
                    subjects: subjects
                });
                alert('Timetable saved successfully!');
            } catch (error) {
                console.error('Error saving timetable:', error);
            } finally {
                setLoading(false);
            }
        }
    };

    const handleSubjectChange = (hour, subject) => {
        setSubjects((prevSubjects) => ({
            ...prevSubjects,
            [hour]: subject
        }));
    };

    const handleChangeHoursPerDay = (event) => {
        setHoursPerDay(event.target.value);
    };

    return (
        <Box sx={{ padding: 2 }}>
            <Typography variant="h6" gutterBottom>Manage Your Timetable</Typography>

            <TextField
                label="Hours per Day"
                value={hoursPerDay}
                onChange={handleChangeHoursPerDay}
                type="number"
                fullWidth
                margin="normal"
            />

            <Grid container spacing={2}>
                {[...Array(hoursPerDay).keys()].map(hourIndex => (
                    <Grid item xs={12} sm={3} key={hourIndex}>
                        <TextField
                            label={`Hour ${hourIndex + 1}`}
                            fullWidth
                            variant="outlined"
                            value={subjects[hourIndex + 1] || ''}
                            onChange={(e) => handleSubjectChange(hourIndex + 1, e.target.value)}
                        />
                    </Grid>
                ))}
            </Grid>

            <Box sx={{ marginTop: 2 }}>
                <Button
                    variant="contained"
                    color="primary"
                    onClick={handleSaveTimetable}
                    disabled={loading}
                >
                    {loading ? <CircularProgress size={24} /> : 'Save Timetable'}
                </Button>
            </Box>

            {currentTimetable && (
                <Box sx={{ marginTop: 3 }}>
                    <Typography variant="h6">Your Current Timetable</Typography>
                    <Grid container spacing={2}>
                        {[...Array(hoursPerDay).keys()].map(hourIndex => (
                            <Grid item xs={12} sm={3} key={hourIndex}>
                                <Typography variant="body1">
                                    {`Hour ${hourIndex + 1}: ${currentTimetable.subjects[hourIndex + 1] || 'N/A'}`}
                                </Typography>
                            </Grid>
                        ))}
                    </Grid>
                </Box>
            )}
        </Box>
    );
};

export default TimetableForm;
